import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  const [notes, setNote ] = useState([{
    title:"",
    content:""
  }]);


  function createNote(noteTitle,noteContent ){
    setNote(prevNotes => {
      return [...prevNotes,
         {title: noteTitle,
          content: noteContent
        }]
    })
  }



  return (
    <div>
      <Header />
      <CreateArea onClick="creatNote" onChange="handleNoteChange"/>

      {notes.map(note =>{
        return(<Note key={1} title="Note title" content="Note content"  />)
      })}
      <Footer />
    </div>
  );
}

export default App;
