import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  const [inputText, setInputText] = useState({
    title:"",
    content:""
  });
  const [notes, setNote ] = useState([]);


  function createNote(noteTitle,noteContent ){
    console.log(notes);
    setNote(prevNotes => {
      return [...prevNotes,
         {title: noteTitle,
          content: noteContent
        }]
    })
  }

  function deleteNote(key){
    notes.filter((note,index) =>{
      return (index !== key);
    })

  }

  return (
    <div>
      <Header />
      <CreateArea onClick="createNote" />

      {notes.map((note, index) =>{
        return(<Note key={index} title={note.title} content={note.content} onClick={deleteNote}  />)
      })}
      <Footer />
    </div>
  );
}

export default App;
